const INITIAL_STATE = {
  sections: [
    {
      title: "mens",
      imageUrl: "https://i.ibb.co/R70vBrQ/men.png",
      size: "large",
      id: 1,
      linkUrl: "shop/mens",
    },
    {
      title: "womens",
      imageUrl: "https://i.ibb.co/GCCdy8t/womens.png",
      size: "large",
      id: 2,
      linkUrl: "shop/womens",
    },
    {
      title: "sneakers",
      imageUrl:
        "https://images.unsplash.com/photo-1508125207376-764c000e04c7?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1951&q=80",
      id: 3,
      linkUrl: "shop/sneakers",
    },
    {
      title: "jackets",
      imageUrl: "https://i.ibb.co/px2tCc3/jackets.png",
      id: 4,
      linkUrl: "shop/jackets",
    },
    {
      title: "hats",
      imageUrl:
        "https://images.unsplash.com/photo-1533055640609-24b498dfd74c?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=967&q=80",
      id: 5,
      linkUrl: "shop/hats",
    },
  ],
};

const directoryReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    default:
      return state;
  }
};

export default directoryReducer;
