import { createGlobalStyle } from "styled-components";

export const GlobalStyle = createGlobalStyle`
body {
    font-family: "Lobster", cursive;
    padding: 0;
    background: #f4f6ff;
    
    @media screen and (max-width: 800px) {
        padding: 0 10px;
    }
  }
  
  a {
    text-decoration: none;
    color: black;
  }

  hr {
    overflow: visible;
    height: 30px;
    border-style: solid;
    border-color: black;
    border-width: 1px 0 0 0;
    border-radius: 20px;
  }

  hr:before {
    display: block;
    content: "";
    height: 30px;
    margin-top: -31px;
    border-style: solid;
    border-color: black;
    border-width: 0 0 1px 0;
    border-radius: 20px;
  }
  
  * {
    box-sizing: border-box;
  }
`;
