import { createGlobalStyle } from "styled-components";

export const GlobalStyle = createGlobalStyle`
body {
    font-family: "Lobster", cursive;
    padding: 0;
    background: #f4f6ff;
    
    @media screen and (max-width: 800px) {
        padding: 0 10px;
    }
  }
  
  a {
    text-decoration: none;
    color: black;
  }

  hr{
    color #127681;
    width: 100%;
  }
  
  * {
    box-sizing: border-box;
  }
`;
